<?php

session_start();

require_once 'config.php';

// Путь к изображению по умолчанию
$user_picture = '';
$redirect_path = './auth.php'; // По умолчанию пользователь перенаправляется на страницу авторизации

if (isset($_GET['code'])) {
    try {
        $token = $client->fetchAccessTokenWithAuthCode($_GET['code']);
        if (isset($token['error'])) {
            throw new Exception('Error fetching access token: ' . htmlspecialchars($token['error']));
        }

        $client->setAccessToken($token['access_token']);
        $google_oauth = new Google_Service_Oauth2($client);
        $google_account_info = $google_oauth->userinfo->get();

        // Сбор информации о пользователе
        $userinfo = [
            'email' => $google_account_info->email,
            'first_name' => $google_account_info->givenName,
            'last_name' => $google_account_info->familyName ?? "",
            'full_name' => $google_account_info->name,
            'picture' => $google_account_info->picture,
            'verifiedEmail' => $google_account_info->verifiedEmail,
            'google_id' => $google_account_info->id
        ];

        // Проверка наличия пользователя в БД и регистрация/авторизация
        if ($stmt = $conn->prepare("SELECT * FROM users WHERE email = ?")) {
            $stmt->bind_param("s", $userinfo['email']);
            $stmt->execute();
            $result = $stmt->get_result();
            if ($result->num_rows > 0) {
                $userdata = $result->fetch_assoc();
                $_SESSION['user_id'] = $userdata['id'];
            } else {
                if ($stmt = $conn->prepare("INSERT INTO users (email, first_name, last_name, full_name, picture, verifiedEmail, google_id) VALUES (?, ?, ?, ?, ?, ?, ?)")) {
                    $stmt->bind_param("ssssssi", $userinfo['email'], $userinfo['first_name'], $userinfo['last_name'], $userinfo['full_name'], $userinfo['picture'], $userinfo['verifiedEmail'], $userinfo['google_id']);
                    $stmt->execute();
                    $_SESSION['user_id'] = $conn->insert_id;
                } else {
                    throw new Exception("Failed to prepare the user insert statement.");
                }
            }
        } else {
            throw new Exception("Failed to prepare the user select statement.");
        }

        // Переадресация на главную страницу
        header("Location: index.php");
        exit;
    } catch (Exception $e) {
        die($e->getMessage());
    }
}

// Загрузка пользовательского изображения
if (isset($_SESSION['user_id'])) {
    // Если в сессии есть user_id, пользователь уже авторизован
    $user_id = $_SESSION['user_id'];
    $redirect_path = './profile.php';

    try {
        $stmt = $conn->prepare("SELECT picture FROM users WHERE id = ?");
        $stmt->bind_param("i", $user_id);
        $stmt->execute();
        $result = $stmt->get_result();
        if ($row = $result->fetch_assoc()) {
            $user_picture = htmlspecialchars($row['picture']);
        }
    } catch (mysqli_sql_exception $e) {
        error_log($e->getMessage());
    }
}



