<script>
// Функция debounce для задержки выполнения фильтрации
function debounce(func, wait) {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
}

const searchInput = document.getElementById('searche');
const animeList = document.querySelector('.anime_list_searche');
const animeElements = document.querySelectorAll('.anime_el');

// Функция для выполнения поиска
function executeSearch() {
  const searchText = searchInput.value.toLowerCase();
  let matches = false;

  animeElements.forEach(function(el) {
    const title = el.getAttribute('data-title').toLowerCase();
    if (title.includes(searchText)) {
      el.style.display = '';
      matches = true;
    } else {
      el.style.display = 'none';
    }
  });

  animeList.style.display = matches ? 'block' : 'none';
}

// Обёртываем функцию поиска debounce функцией для добавления задержки
const debouncedSearch = debounce(executeSearch, 300); // 300 мс задержка

searchInput.addEventListener('input', debouncedSearch);






document.addEventListener('DOMContentLoaded', function() {
    const list = document.querySelector('.episode_list');

    if (list) {
        list.addEventListener('wheel', function(event) {
            if (event.deltaY != 0) { // Если есть вертикальная прокрутка
                event.preventDefault(); // Предотвращаем стандартное поведение скроллинга
                const scrollAmount = event.deltaY * 1; // Корректировка скорости прокрутки
                // Используем scrollBy для добавления плавности к прокрутке
                list.scrollBy({ left: scrollAmount, behavior: 'smooth' });
            }
        });
    }
});
</script>
